import React, { useState } from "react";
import { FlatList, View, Text, Image, TouchableOpacity, ScrollView, Dimensions } from 'react-native';
import Icon from '@expo/vector-icons/Ionicons';
import styles from '../../styles/mainStyle';

const { width, height } = Dimensions.get('window');

const TopContent = () => {
    return (
        <View style={[styles.row, styles.pd10, styles.bdbt1, styles.mgbt20]}> 
            <View style={[styles.fl1, styles.textCenter]}>
                <Image source={{ uri: 'https://cdn.pixabay.com/photo/2015/10/05/22/37/blank-profile-picture-973460_1280.png' }} style={[styles.coim, { width: 65, height: 65 }]} />
            </View>
            <View style={[styles.fl3, styles.textCenter, styles.pdlt10]}>
                <Text style={[styles.f18, styles.b]}>Shuja Khalid</Text>
                <Text style={[styles.f14, styles.pdtp5]}>Lorem ipsum sit amore.</Text>
                <Text style={[styles.f14]}>552211</Text>
            </View>
            <View style={[styles.flsemi]}>
                <Text>
                    <Icon name="chevron-forward-circle-outline" size={22} />
                </Text>
            </View>
        </View>
    );
};

